for id in `cat all_segs_by_id.txt`; do id="${id#"${id%%[![:space:]]*}"}"; id="${id%"${id##*[![:space:]]}"}"; echo "select substring(sample.stable_id, 1, 12) as  sample_id, seg.chr as chr, seg.start as start, seg.end as end, seg.num_probes as num_probes, seg.segment_mean as segment_mean from sample sample left join copy_number_seg seg on sample.internal_id = seg.sample_id  where substring(sample.stable_id,1,12) = \"${id}\"; " | mysql -ucbio_user -psomepassword -Dcbioportal >${id}.seg.txt; done;
